mapper.select:
select
u.*,
a.total,
a.able_money,
a.unable_money,
a.collection,
a.continue_total,
a.award_money,
a.taste_money,
a.investor_collection_capital,
a.investor_collection_interest,
a.borrower_collection_capital,
a.borrower_collection_interest
from fy_user u left join fy_user_account a on u.id = a.user_id
where 1 = 1
 and u.id = #{id}
 and u.username = #{username} 
and u.password = #{password} 
and u.email = #{email} 
and u.phone = #{phone}
and u.card_id = #{cardId}
and u.real_status = #{realStatus}
and u.type_id = #{typeid} 
and u.email_status = #{emailStatus} 
and u.email_freq = #{emailFreq} 
and u.is_enabled = #{isEnabled}

select u.*
from fy_user u
where u.id = #{id}

mapper.update:
update fy_user_award set
status = 1
,award_name = #{awardName}
,award_name_code = #{awardNameCode}
,user_id = #{userId}
,name = #{name}
,phone = #{phone}
,address = #{address}
where 	award_code = #{awardCode}

update fy_user set
	login_time=#{loginTime},
	last_time=#{lastTime},
	last_ip=#{lastIp},
	modify_date=now(),
	login_failure_count=#{loginFailureCount},
	is_lock=#{isLock},
	locked_date=#{lockedDate},
	device_token=#{deviceToken}
	,tg_no=#{tgNo}
	,gesture=#{gesture}
where 
	id = #{id}

update fy_user set
	modify_date=#{modifyDate},
	litpic=#{litpic}
where 
	id = #{id}

update fy_user set
	modify_date=now(),
	type_id=#{typeId}
	,email_freq=#{emailFreq}
where 
	id = #{id}

mapper.select:
select
*
from fy_account_bank where user_id=#{userId} order by id desc;

SELECT t.*, (SELECT IFNULL(SUM(s.tg_money),0) FROM fy_user_spread_detail s WHERE t.id = s.`tender_user_id` AND s.status_code = #{tgType}) AS sumTgMoney, (select IFNULL(sum(uad.money),0) from fy_user_award_detail uad where uad.relate_to = t.id and uad.relate_key = 'user_id' and uad.type = 'money_log_friend_name_email_tender_award') as sumRegisterMoney, (SELECT MIN(fbd.id) FROM fy_borrow_detail fbd LEFT JOIN fy_borrow fb ON fb.id = fbd.borrow_id WHERE fb.type !=20 AND fb.type !=10 AND fbd.user_id = t.id ) as mbdid FROM fy_user t
ORDER BY ${orderBy}
limit #{pager.thisSize},#{pager.pageSize}

mapper.update:
update fy_user set
modify_date=#{modifyDate},
phone_code=#{phoneCode}
where 
	id = #{id}

mapper.select:
SELECT u.id, u.`username`, u.`create_date` as createDate, u.`real_status` as realStatus, u.`email_status` as emailStatus, c.bdSumMoney, d.uadSumMoney FROM fy_user u LEFT JOIN (SELECT bd.`user_id` AS bdUserId, SUM(bd.`account`) AS bdSumMoney FROM fy_borrow_detail bd GROUP BY bd.`user_id`) c ON c.bdUserId = u.id LEFT JOIN (SELECT uad.relate_to AS uadRelateTo,SUM(uad.money) AS uadSumMoney FROM fy_user_award_detail uad WHERE uad.`type`='tui_detail_award' GROUP BY uad.relate_to ) d ON d.uadRelateTo = u.id
ORDER BY ${orderBy}
limit #{pager.thisSize},#{pager.pageSize}

mapper.update:
update fy_user set 
	token=#{token},
	last_time=#{lastTime}
where
	id = #{id}
